var TextSizeUtil={textSizes:Array("","large","larger"),textSizeIncreaseClass:".increaseTextSize",textSizeDecreaseClass:".decreaseTextSize",textSizeInactiveClass:"inactive",textSizeCookieName:"siteTextSize",currentTextSize:0,createCookie:function(e,t,n){if(n){var r=new Date;r.setTime(r.getTime()+n*24*60*60*1e3);var i="; expires="+r.toGMTString()}else var i="";document.cookie=escape(e)+"="+escape(t)+i+"; path=/"},readCookie:function(e){var t=escape(e)+"=";var n=document.cookie.split(";");for(var r=0;r<n.length;r++){var i=n[r];while(i.charAt(0)==" ")i=i.substring(1,i.length);if(i.indexOf(t)==0)return unescape(i.substring(t.length,i.length))}return null},eraseCookie:function(e){this.createCookie(e,"",-1)},updateTextSize:function(e,t,n){if(e>0){if(this.textSizes[this.currentTextSize].length>0){$("body").removeClass(this.textSizes[this.currentTextSize])}this.currentTextSize+=t;$("body").addClass(this.textSizes[this.currentTextSize]);if(!n){if(this.currentTextSize==0){this.eraseCookie(this.textSizeCookieName)}else{this.createCookie(this.textSizeCookieName,this.currentTextSize,365)}}if(this.currentTextSize==0){$(this.textSizeDecreaseClass).addClass(this.textSizeInactiveClass)}else{$(this.textSizeDecreaseClass).removeClass(this.textSizeInactiveClass);if(this.currentTextSize==this.textSizes.length-1){$(this.textSizeIncreaseClass).addClass(this.textSizeInactiveClass)}else{$(this.textSizeIncreaseClass).removeClass(this.textSizeInactiveClass)}}}},init:function(){var e=this.readCookie(this.textSizeCookieName);e=e&&!isNaN(e)?parseInt(e):0;this.updateTextSize(TextSizeUtil.textSizes.length-1,e,true);$(this.textSizeIncreaseClass).click(function(e){e.preventDefault();TextSizeUtil.updateTextSize(TextSizeUtil.textSizes.length-TextSizeUtil.currentTextSize-1,1)});$(this.textSizeDecreaseClass).click(function(e){e.preventDefault();TextSizeUtil.updateTextSize(TextSizeUtil.currentTextSize,-1)})}};$(document).ready(function(){TextSizeUtil.init()});